ges\":[],\"data\":{\"text-align\":\"start\"}},{\"key\":\"5nspa\",\"text\":\"\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}},{\"key\":\"8r6g7\",\"text\":\"Jefe de Major\",\"type\":\"header-two\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":13,\"style\":\"fontfamily-Arial\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"start\"}},{\"key\":\"3gvda\",\"text\":\"\\\"El Major de Geociencias de la UC, te permitirá descubrir el mundo de la Geología y la Geofísica, y su interacción con otras disciplinas.\\\"\",\"type\":\"blockquote\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":138,\"style\":\"color-rgb(51,51,51)\"},{\"offset\":0,\"length\":138,\"style\":\"fontsize-14\"},{\"offset\":0,\"length\":138,\"style\":\"fontfamily-Arial\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"start\"}},{\"key\":\"3lqch\",\"text\":\"Gloria Arancibia Hernández, majorgeociencias@ing.puc.cl\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":55,\"style\":\"color-rgb(51,51,51)\"},{\"offset\":0,\"length\":55,\"style\":\"fontsize-14\"},{\"offset\":0,\"length\":55,\"style\":\"fontfamily-Arial\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"right\"}},{\"key\":\"9p6c8\",\"text\":\"\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}},{\"key\":\"41f42\",\"text\":\" \",\"type\":\"atomic\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":1,\"style\":\"fontfamily-Arial\"}],\"entityRanges\":[{\"offset\":0,\"length\":1,\"key\":0}],\"data\":{}},{\"key\":\"85db9\",\"text\":\"\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}}],\"entityMap\":{\"0\":{\"type\":\"IMAGE\",\"mutability\":\"MUTABLE\",\"data\":{\"src\":\"http://www.ing.uc.cl/wp-content/uploads/2014/05/geociencias3.png\",\"height\":\"640px\",\"width\":\"800px\",\"alignment\":\"none\"}}}}",
