:[{\"offset\":0,\"length\":177,\"style\":\"color-rgb(51,51,51)\"},{\"offset\":0,\"length\":177,\"style\":\"fontsize-14\"},{\"offset\":0,\"length\":177,\"style\":\"fontfamily-Arial\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"start\"}},{\"key\":\"40i0e\",\"text\":\"Claudio Mourgues Álvarez, mia@ing.puc.cl\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":40,\"style\":\"color-rgb(51,51,51)\"},{\"offset\":0,\"length\":40,\"style\":\"fontsize-14\"},{\"offset\":0,\"length\":40,\"style\":\"fontfamily-Arial\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"right\"}},{\"key\":\"28md1\",\"text\":\"\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}},{\"key\":\"4sc3t\",\"text\":\" \",\"type\":\"atomic\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":1,\"style\":\"fontfamily-Arial\"}],\"entityRanges\":[{\"offset\":0,\"length\":1,\"key\":0}],\"data\":{}},{\"key\":\"6g4aq\",\"text\":\"\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}}],\"entityMap\":{\"0\":{\"type\":\"IMAGE\",\"mutability\":\"MUTABLE\",\"data\":{\"src\":\"http://www.ing.uc.cl/wp-content/uploads/2014/05/ing--y-arquitectura.png\",\"height\":\"640px\",\"width\":\"800px\",\"alignment\":\"none\"}}}}",
